import styled from 'styled-components';

const Sidebar = styled.div`
  background-color: ${(props) => props.theme.offWhite};
  grid-area: sidebar;
  border-bottom-left-radius: 8px;
  box-shadow: 2px 0px 16px -2px rgba(0, 0, 0, 0.15);
`;

export default Sidebar;
