import styled from 'styled-components';

const MenuItem = styled.li`
  font-size: 1.6rem;
  line-height: 1;
  text-transform: uppercase;
  color: ${(props) => props.theme.primaryDark};
  padding-left: 7.2rem;

  ion-icon {
    font-size: 1.8rem;
    margin-right: 2rem;
  }

  a {
    padding
  }
`;

export default MenuItem;
