import React, { useState, useEffect } from 'react';
import MainContent from './styles/MainContent';
import Form from './styles/Form';
import FormGroup from './styles/FormGroup';
import FormInput from './styles/FormInput';
import FormLabel from './styles/FormLabel';

const EditItem = ({ item }) => {
  const [name, setName] = useState('');
  const [link, setLink] = useState('');

  useEffect(() => {
    setName(item.name);
    setLink(item.link);
  });

  return (
    <MainContent>
      <Form
      // onSubmit={(e) => {
      //   e.preventDefault();
      //   console.log(name);
      //   submitForm({ pricePerItem, link });
      // }}
      >
        <FormGroup>
          <FormLabel htmlFor="name">Product name</FormLabel>
          <FormInput
            type="text"
            placeholder="Enter product name..."
            id="name"
            name="name"
            onChange={(e) => setName(e.target.value)}
            value={name}
          />
        </FormGroup>
        <FormGroup>
          <FormLabel htmlFor="name">Product Link</FormLabel>
          <FormInput
            type="text"
            placeholder="Enter product link..."
            id="link"
            value={link}
            onChange={(e) => setLink(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <FormLabel htmlFor="pricePerItem">Price per item</FormLabel>
          <FormInput
            type="text"
            placeholder="Enter price..."
            id="pricePerItem"
            value={pricePerItem}
            onChange={(e) => setPrice(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <FormLabel htmlFor="tax">Tax</FormLabel>
          <FormInput type="text" placeholder="Enter tax..." id="tax" />
        </FormGroup>
        <FormGroup>
          <FormLabel htmlFor="pricePerItem">
            Enter tax to charge customer
          </FormLabel>
          <FormInput
            type="text"
            placeholder="Enter price..."
            id="taxForCustomer"
          />
        </FormGroup>
        <FormGroup>
          <FormLabel htmlFor="usShippingFee">usShippingFee</FormLabel>
          <FormInput
            type="text"
            placeholder="Enter price..."
            id="usShippingFee"
          />
        </FormGroup>
        <FormGroup>
          <FormLabel htmlFor="quantity">quantity</FormLabel>
          <FormInput type="text" placeholder="Enter price..." id="quantity" />
        </FormGroup>
        <FormGroup>
          <FormLabel htmlFor="estimatedWeight">Estimated weight</FormLabel>
          <FormInput
            type="text"
            placeholder="Enter price..."
            id="estimatedWeight"
          />
        </FormGroup>
        <FormGroup>
          <FormLabel htmlFor="orderedWebsite">order Website</FormLabel>
          <FormInput
            type="text"
            placeholder="Enter price..."
            id="orderedWebsite"
          />
        </FormGroup>

        <FormGroup>
          <FormLabel htmlFor="orderAccount">orderAccount</FormLabel>
          <FormInput
            type="text"
            placeholder="Enter price..."
            id="orderAccount"
          />
        </FormGroup>

        <button>Submit</button>
      </Form>
    </MainContent>
  );
};

export default EditItem;
