import styled from 'styled-components';

const MenuItem = styled.li`
  font-size: 1.6rem;
  line-height: 1;
  text-transform: uppercase;
  color: ${(props) => props.theme.primaryDark};

  ion-icon {
    font-size: 1.8rem;
    margin-right: 2rem;
  }

  a {
    padding: 2rem 0 2rem 7.2rem;
    width: 100%;
    background-color: salmon;
    display: block;
  }
`;

export default MenuItem;
