import styled from 'styled-components';

const Container = styled.div`
  height: 95vh;
  width: 90%;
  background-color: ${(props) => props.theme.offWhite};
  border-radius: 8px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
`;

export default Container;
