import React from 'react';

const ItemRow = ({ item, index }) => {
  return (
    <tr
      key={item.id}
      onClick={() => setShowDetail(false)}
      style={{ backgroundColor: index % 2 === 0 ? '#ececec' : '#fff' }}
    >
      <th>
        {new Date(item.createdAt).toLocaleString('en-us', {
          month: 'long',
          year: 'numeric',
          day: 'numeric',
        })}
      </th>
      <th>
        <a href={item.trackingLink}>{item.trackingLink}...</a>
      </th>
      <th>
        <a href={item.link}>{item.link}...</a>
      </th>
      <th>{item.name}</th>
      <th>{item.name}</th>
      <th>
        {new Intl.NumberFormat('de-DE', {
          style: 'currency',
          currency: 'USD',
        }).format(item.pricePerItem)}
      </th>
      <th>{item.quantity}</th>
      <th>{item.status}</th>
      <th>
        {/* <DetailBtn
        onClick={(e) => {
          e.stopPropagation();
