import React, { useEffect, useState } from 'react';
import Loader from './styles/Loader';
import MainContent from './styles/MainContent';
import axios from 'axios';
import DetailList from './styles/DetailList';
import DetailItem from './styles/DetailItem';
import DetailItemTitle from './styles/DetailItemTitle';
import DetailItemInfo from './styles/DetailItemInfo';

const SingleItem = (props) => {
  const [item, setItem] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchData() {
      try {
        const res = await axios.get(
          `http://localhost:4444/api/v1/items/${props.id}`
        );

        console.log(res);
        setItem(res.data.data.data);
        setLoading(false);
      } catch (err) {
        console.log(err);
      }
    }

    if (props.id !== undefined) {
      fetchData();
    }
  }, [props.id]);

  return loading ? (
    <Loader />
  ) : (
    <MainContent>
      <DetailList>
        <DetailItem>
          <DetailItemTitle>Tên sản phẩm</DetailItemTitle>
          <DetailItemInfo>{item.name}</DetailItemInfo>
        </DetailItem>
        <DetailItem>
          <DetailItemTitle>Giá</DetailItemTitle>
          <DetailItemInfo>{item.pricePerItem}</DetailItemInfo>
        </DetailItem>
        <DetailItem>
          <DetailItemTitle>Số lượng</DetailItemTitle>
          <DetailItemInfo>{item.quantity}</DetailItemInfo>
        </DetailItem>

        <DetailItem>
          <DetailItemTitle>Thuế</DetailItemTitle>
          <DetailItemInfo>{item.tax}</DetailItemInfo>
        </DetailItem>
        <DetailItem>
          <DetailItemTitle>Thuế tính cho khách</DetailItemTitle>
          <DetailItemInfo>{item.taxForCustomer}</DetailItemInfo>
        </DetailItem>
        <DetailItem>
          <DetailItemTitle>Cân nặng ước tính</DetailItemTitle>
          <DetailItemInfo>{item.estimatedweight}</DetailItemInfo>
        </DetailItem>
        <DetailItem>
          <DetailItemTitle>Cân nặng thực tế</DetailItemTitle>
          <DetailItemInfo>{item.actualWeight}</DetailItemInfo>
        </DetailItem>
        <DetailItem>
          <DetailItemTitle>Tên sản phẩm</DetailItemTitle>
          <DetailItemInfo>{item.name}</DetailItemInfo>
        </DetailItem>
        <DetailItem>
          <DetailItemTitle>Tên sản phẩm</DetailItemTitle>
          <DetailItemInfo>{item.name}</DetailItemInfo>
        </DetailItem>
      </DetailList>
    </MainContent>
  );
};
export default SingleItem;
