import styled from 'styled-components';

const Container = styled.div`
  height: 95vh;
  width: 90%;
  background-color: ${(props) => props.theme.primaryLight};
  border-radius: 8px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  display: grid;
  grid-template-areas:
    'header'
    'sidebar main';
`;

export default Container;
