import styled from 'styled-components';

const MenuItem = styled.li`
  font-size: 1.6rem;
  line-height: 1;
  font-weight: 700;
  color: ${(props) => props.theme.primaryDark};

  ion-icon {
    font-size: 1.8rem;
    margin-right: 2rem;
    top: 10px;
  }

  a {
    padding: 1.6rem 0 2rem 7.2rem;
    width: 100%;
    display: block;
  }
`;

export default MenuItem;
