import React, { useState } from 'react';
import styled from 'styled-components';

const Detail = styled.div`
  background-color: #fff;
  box-shadow: 2px 2px 18px -2px rgba(0, 0, 0, 0.2);
  border-radius: 4px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-95%, 15%);
  padding: 0.6rem 0rem;
  text-align: left;
  opacity: 0;
  visibility: hidden;
  z-index: 1000;

  &.show {
    opacity: 1;
    visibility: visible;
  }

  li {
    width: 15rem;
    cursor: pointer;
    border-left: 4px solid transparent;
    display: flex;

    a {
      width: 100%;
      height: 100%;
      padding: 1rem 0rem 1rem 1.8rem;
    }
  }

  li:hover {
    background-color: ${(props) => props.theme.primaryLight};
    border-left: 4px solid ${(props) => props.theme.primaryDark};
  }
`;

const DetailBtn = styled(Button)`
  width: 4rem;
  height: 4rem;
  padding: 1rem;
  background-color: transparent;

  :hover {
    background-color: ${(props) => props.theme.primaryLight};
  }
`;

const Button = styled.button`
  &,
  &:link,
  &:visited {
    text-transform: uppercase;
    text-decoration: none;

    display: inline-block;
    border-radius: 10rem;
    transition: all 0.2s;
    position: relative;
    font-size: $default-font-size;

    border: none;
    cursor: pointer;
  }

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 1rem 2rem rgba($color-black, 0.2);

    &::after {
      transform: scaleX(1.4) scaleY(1.6);
      opacity: 0;
    }
  }

  &:active,
  &:focus {
    outline: none;
    transform: translateY(-1px);
    box-shadow: 0 0.5rem 1rem rgba($color-black, 0.2);
  }
`;

const ItemRow = ({ item, index }) => {
  const [showDetail, setShowDetail] = useState(false);
  return (
    <tr
      key={item.id}
      onClick={() => setShowDetail(false)}
      // style={{ backgroundColor: index % 2 === 0 ? '#ececec' : '#dae1e7' }}
    >
      <th>
        {new Date(item.createdAt).toLocaleString('en-us', {
          month: 'long',
          year: 'numeric',
          day: 'numeric',
        })}
      </th>
      <th>
        {item.trackingLink ? (
          <a href={item.trackingLink}>Click here</a>
        ) : (
          <p>No link available</p>
        )}
      </th>
      <th>
        {item.link ? (
          <a href={item.link}>Click here</a>
        ) : (
          <p>No link available</p>
        )}
      </th>
      <th>{item.name}</th>
      <th>{item.name}</th>
      <th>
        {new Intl.NumberFormat('de-DE', {
          style: 'currency',
          currency: 'USD',
        }).format(item.pricePerItem)}
      </th>
      <th>{item.quantity}</th>
      <th>{item.status}</th>
      <th>
        <DetailBtn
          onClick={(e) => {
            e.stopPropagation();
            setShowDetail(!showDetail);
          }}
        >
          ...
        </DetailBtn>
        <Detail className={showDetail ? 'show' : ''}>
          <ul>
            <li>
              <Link href={`/items/${item.id}`}>
                <a>View</a>
              </Link>
            </li>
            <li>
              <Link href="/">
                <a>Edit</a>
              </Link>
            </li>
            <li>
              <Link href="/">
                <a>Delete</a>
              </Link>
            </li>
          </ul>
        </Detail>
      </th>
    </tr>
  );
};

export default ItemRow;
